let scale = 1;

// Open modal
images.forEach(img => {
  img.addEventListener("click", () => {
    img.classList.remove("blur");
    openModal(img);
  });
});

function openModal(clickedImg) {
  modal.style.display = "block";
  popupImg.src = clickedImg.src;
  caption.innerText = clickedImg.alt;
  scale = 1;
  popupImg.style.transform = "scale(1)";
  loadThumbnails(clickedImg);
}

// Load thumbnails INSIDE popup
function loadThumbnails(activeImg) {
  thumbContainer.innerHTML = "";

  images.forEach(img => {
    const thumb = img.cloneNode();
    thumb.classList.remove("blur");

    thumb.onclick = () => {
      popupImg.src = thumb.src;
      caption.innerText = thumb.alt;
      scale = 1;
      popupImg.style.transform = "scale(1)";
    };

    thumbContainer.appendChild(thumb);
  });
}

// Zoom
zoomIn.onclick = () => {
  scale += 0.2;
  popupImg.style.transform = `scale(${scale})`;
};

zoomOut.onclick = () => {
  scale = Math.max(1, scale - 0.2);
  popupImg.style.transform = `scale(${scale})`;
};

// Close modal
closeBtn.onclick = () => modal.style.display = "none";

document.querySelectorAll(".card img").forEach(img => {
  img.addEventListener("click", () => {
    const key = img.dataset.key;
    const data = imageWidget[key];

    popupImg.src = data.images[0];
    caption.innerText = data.title;
    thumbContainer.innerHTML = "";

    data.images.forEach(src => {
      const thumb = document.createElement("img");
      thumb.src = src;
      thumb.className = "thumb";
      thumb.onclick = () => popupImg.src = src;
      thumbContainer.appendChild(thumb);
    });

    modal.style.display = "block";
  });
});


